import './App.css';
import React, { useState, useEffect } from 'react';
import {getTransactions, deleteTransactions, editTransactions, addTransactions} from './api'
import 'bootstrap/dist/css/bootstrap.min.css';

import EntitysList from './components/EntitysList/EntitysList';
import TransactionsList from './components/TransactionsList/TransactionsList';

function App() {

  const [activeEntity, setActiveEntityState] = useState('transactions');
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    getTransactions().then(res => {
      setTransactions(res.data.data);
    })
    .catch(err => {
      console.log('error getting transactions',err)
    })
  },[]);

  function setActiveEntity(entity){
    setActiveEntityState(entity);
  }

  function deleteItem(itemId){
    //delete from server
    deleteTransactions(itemId);

    //delete in client
    let transactionsCopy = [...transactions];
    let index = transactionsCopy.findIndex(item => item._id === itemId);
    transactionsCopy.splice(index,1);
    setTransactions(transactionsCopy);
  }

  function editItem(editedItem){
    //edit server
    editTransactions(editedItem);

    //edit client
    let transactionsNew = transactions.map(transaction => transaction._id === editedItem._id ? editedItem : transaction)
    setTransactions(transactionsNew);
  }

  function addItem(newItem){
    //add server
    addTransactions(newItem);

    //add client
    let transactionsCopy = [...transactions];
    transactionsCopy.push(newItem);
    setTransactions(transactionsCopy);
  }

  return (
    <div className="App">
      <h1>Billing App</h1>
      <div className={'content-container'}>
        <div className={'menu-container'}>
          <EntitysList 
            activeEntity={activeEntity}
            setActiveEntity = {setActiveEntity}
          />
        </div>
        <div className={'data-container'}>
            <TransactionsList
              transactions={transactions}
              deleteItem={(itemId)=>deleteItem(itemId)}
              editItem={(editedItem)=>editItem(editedItem)}
              addItem={(newItem)=>addItem(newItem)}
            />
        </div>
      </div>

    </div>
  );
}

export default App;
