import './DetailsModal.css';

import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function DetailsModal({transaction,closeDetails}) {
console.log(transaction)
  return (
    <div className="detailsModalContainer">
      <Modal.Dialog scrollable>
        <Modal.Header closeButton onClick={closeDetails}>
            <Modal.Title>Transaction details</Modal.Title>
        </Modal.Header>

        <Modal.Body className={'modal-details-body'}>
            {/* <h4>{`${transaction.first_name} ${transaction.last_name}`}</h4>
            <p>{`Total Price: ${transaction.total_price} ${transaction.currency}`}</p>
            <p>{`Address: ${transaction.country}, ${transaction.city}, ${transaction.street}`}</p>
            <p>{`Credit Card: ${transaction.cerdit_card_number}, ${transaction.cerdit_card_type}`}</p>
            <p></p> */}
            <form onSubmit={this.handleSubmit}>
                <label>
                Name:
                <input type="text" value={this.state.value} onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />
            </form>
        </Modal.Body>

        <Modal.Footer>
            <Button variant="secondary" onClick={closeDetails}>Close</Button>
            <Button variant="primary">Save changes</Button>
        </Modal.Footer>
      </Modal.Dialog>
    </div>
  );
}

export default DetailsModal;
